import { Component, HostListener, OnInit } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent implements OnInit {
  activeLink = 'home';
  scrolled = false;

  constructor() { }

  ngOnInit(): void {
  }

  @HostListener('window:scroll', ['$event'])
  onWindowScroll(): void {
    this.scrolled = window.scrollY > 50;
  }

  onUpdateActiveLink(value: string): void {
    this.activeLink = value;
  }

  connect(): void {
    console.log('connect');
  }
}
<nav class="navbar navbar-expand-lg" [ngClass]="{ 'scrolled': scrolled }">
  <div class="container">
    <a class="navbar-brand" href="#home"><img src="assets/img/logo.svg" alt="Logo" /></a>
    <button class="navbar-toggler" type="button" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link" routerLink="/home" routerLinkActive="active" (click)="onUpdateActiveLink('home')">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/book-list" routerLinkActive="active" (click)="onUpdateActiveLink('skills')">Book CMS</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/" routerLinkActive="active" (click)="onUpdateActiveLink('projects')">Projects</a>
        </li>
      </ul>
      <div class="navbar-text">
        <div class="social-icon">
          <a href="#"><img src="assets/img/nav-icon1.svg" alt="" /></a>
          <a href="#"><img src="assets/img/nav-icon2.svg" alt="" /></a> 
          <a href="#"><img src="assets/img/nav-icon3.svg" alt="" /></a>
        </div>
        <button class="vvd" (click)="connect()"><span>Let's Connect</span></button>
      </div>
    </div>
  </div>
</nav>
